import React, { useRef } from "react";
import '../Ejercicio1/style.css';

function Botones(){

    const destino = useRef(null);
    const elenco = useRef(null);
    const extra = useRef(null);

    const btnElenco =()=>{

        destino.current.parentNode.children[2].classList.toggle("divele")
        console.log( destino.current.parentNode.children[2])
        destino.current.parentNode.children[2].classList.setAttribute("class", "prueba")
       
        console.log( destino.current.parentNode.children[2])
    }

    const btnExtra =()=>{
     destino.current.parentNode.children[3].classList.toggle("divext");
     destino.current.parentNode.children[3].classList.setAttribute("class", "prueba")
        destino.current.parentNode.children[3].classList.toggle("divext");
        console.log( destino.current.parentNode.children[3])


    }

    return(

        <React.Fragment>
            <div className="btn" ref={destino} >
            <button className="elenco" ref={elenco} onClick= {btnElenco}> ELENCO</button>
            <button className="extra" ref={extra} onClick= {btnExtra}>EXTRA</button>
            </div>
        </React.Fragment>

    );
}

export default Botones;
