import React, { useState } from "react";
import { crearAleatorio } from "../Biblioteca/Aleatorios/aleatorios.js";

function Aleatorio() {
  //Valor inicial a vacío;
  const valorInicial = [];

  //Pongo el estado a valor inicial
  const [numeros, setNumeros] = useState(valorInicial);

  //Función que añade un aleatorio no repetido al estado.
  const addNumero = () => {
    let numeroAleatorio;

    do {
      numeroAleatorio = crearAleatorio(1, 100);
    } while (numeros.includes(numeroAleatorio));

    setNumeros([...numeros, numeroAleatorio]);
  };

  //Función que borra un número seleccionado.
  const borrarNumero = (borraNumero) => {
    const nuevoNumero = numeros.filter((numero) => {
      return numero !== borraNumero;
    });
    setNumeros(nuevoNumero);
  };

  //Función eliminar todos los números
  const borrarLista = () => {
    setNumeros([]);
  };

  return (
    <React.Fragment>
      {/* Botón crear  eliminar*/}
      <h1>Generar lista de aleatorios</h1>
      <button onClick={addNumero}>Generar</button>
      <button onClick={borrarLista}>Eliminar</button>
      <ul>
        {/* Se mapea el array y se pinta el valor */}
        {numeros.map((numero) => {
          return (
            <li key={numero} onClick={() => {
                borrarNumero(numero);}}>
              {numero}
            </li>
          );
        })}
      </ul>
    </React.Fragment>
  );
}

export default Aleatorio;
