import Nota from './Nota.js';
import '../Ejercicio1/style.css'

function Pelicula(props){

    return(
        <div className="">
            <h1>{props.titulo}</h1>
            <p className="directores">{props.director}</p>
            <p>{props.clasificacion}</p>
            <p><img className="imagen" src={props.cartelera} alt="Cartel de la Película"/></p>

            <p>Nota película:
                <Nota nota={props.nota}>
                </Nota>
            </p>
            <p className="resumen">{props.children}</p>
        </div>
    );
}

export default Pelicula;
