import React, { useState, useEffect } from "react";
import "../css/style.css";
import "../css/normaliza.css";
import { obtenerDatos, invertirCadena } from "../Biblioteca/traerDatos.js";
import ListaPeliculas from "./ListaPeliculas.js";
import Pelicula from "./Pelicula";


function Peliculas() {

    const url = "https://swapi.py4e.com/api/films/";
    const valorInicial = [];
    const peliculaInicial = {};
    const actoresInicial = {};
    const  [peliculas, setPeliculas] = useState(valorInicial);
    const  [pelicula , setPelicula] = useState(peliculaInicial);
    const [Actores, setActores] = useState(actoresInicial)

    const getPeliculas = async(origen)=>{
        let datos = await obtenerDatos(origen);
        if(!datos.message){
            setPeliculas(datos.results);
        }
    };

    const getDatosPelicula = async (origen) =>{
        let datos = await obtenerDatos(origen);
        if(!datos.message){
            setPelicula(datos);
        }
    };

    const getActorePelicula = async (origen) =>{
        let datos = await obtenerDatos(origen);
        if(!datos.message){
            setActores(datos.results);
        }
    };

    useEffect(()=>{
        getPeliculas(url);
    },[]);




  

 
  return (
    <React.Fragment>

        <div id="box1" className="item1">
            <ListaPeliculas datos={peliculas} funcion={getDatosPelicula}/> 
        </div>
        <div id="box2" className="item2">
            {Object.keys(pelicula).length !== 0 ?(<Pelicula datos={pelicula}/>
            ):(<h2>Selecciona</h2>)}
        </div>

      
      
    </React.Fragment>
  );
}

export default Peliculas;
