//Importamos los estilos css de la información El JSON el atributo película y la nota.
//import './peliculas.css';
import React from "react";
import PeliculasJson from './Ejercicio2.json';
import Pelicula from './Pelicula';
//import '../Ejercicio1/style.css';
import '../Ejercicio1/style.css'

function Peliculas(props){
    //Devolemos un div que lego tiene un map que recorre Peliculas que es el nombre que le he puesto al JSON y peliculas que es el nombre del array.
    //Y luego ceamos un objeto películas y le ponemos los valores del atriuto v.
    return(
        <div className="">
            {PeliculasJson.peliculas.map((v) => {
                return (
                    <div className='grid-container'>
                        <Pelicula
                            key = {v.id}
                            titulo = {v.nombre}
                            director = {v.director}
                            clasificacion = {v.clasificacion}
                            cartelera = {v.cartelera}
                            nota = {v.nota}
                        >
                            {console.log(v.clasificacion)}
                            <div className='information'> 
                                {v.resumen}
                            </div>
                        </Pelicula>

                    </div>

                );
            })}

        </div>

    );
}

export default Peliculas;
