import React, { useState, useEffect } from "react";
import{colorHex} from "../Biblioteca/colores.js";

function Colores() {

  //Valor inicial a vacío;
  const valorInicial = "";
  const textoInicial = "";
  const elemento = document;

  //Pongo el estado a valor inicial
  const [color, setColor] = useState(valorInicial);
  const [texto, setTexto] = useState(textoInicial);

 

//Función pintar 
  const pintar = ()=>{
    let hexColor = colorHex() ;
    setColor(hexColor);
    setTexto(hexColor);
    elemento.body.style.background = color;

  }


  useEffect(()=>{
    elemento.addEventListener("click", pintar);
    
    return () => {
      elemento.removeEventListener("click", pintar);
    }; 
  },[color]); //No funciona sin la dependencia,
             // el primer clic recoge el color en el texto pero no cambia el color
             //funciona con el segundo clic.

  

  return (
    <React.Fragment>
      <nav>
      <div className="nav-center">
        <ul className="nav-links">
          <li><a href="Colores.js">Random HEX</a></li>
        </ul>
      </div>
    </nav>
      <div className="container">
        <h2>background color hex : <span className="color">{texto}</span></h2>
      </div>
      
    </React.Fragment>
  );
}

export default Colores;
