import React, { useState } from "react";
import {crearAleatorio} from "../Biblioteca/Aleatorios/aleatorios.js"


function Aleatorio(){

//Valor inicial a vacío;
      const valorInicial = [];

  //Pongo el estado a valor inicial
  const [numeros, setNumeros] = useState(valorInicial);

  //Función que añade un aleatorio al estado
  const addNumero = () => {
    let numeroAleatorio; 
    // do {
    //        numeroAleatorio = crearAleatorio(1,100);
    // } while (numeros.includes(numeroAleatorio));
    numeroAleatorio = crearAleatorio(1,100);

    setNumeros([...numeros, numeroAleatorio]);
  };

  //Función que borra un número seleccionado.
  const borrarNumero = (e) => {
    const nuevoNumero = numeros.filter((numero) => {
      return numero!== e.t;
    });
    setNumeros(nuevoNumero);
  };

  //Función eliminar todos los números
  const borrarLista = () => {
    setNumeros([]);
  };

    return(
      <React.Fragment>
         {/* Botón crear */}
           <button onClick={() => {
                addNumero();}}>
            Generar
          </button>

          {/* Botón eliminar */}
          <button onClick={() => {
              borrarLista();}}>
            Eliminar
          </button>
                <ul>
                {numeros.map((numero) => {
            return <li>{numero}</li>;})}
                </ul>
            </React.Fragment>

    );
}

export default Aleatorio;
