import React, { useState, useEffect } from "react";


function Coordenadas() {

  //Valor inicial a vacÃ­o;
  const valorInicialX = 0;
  const valorInicialY = 0;
  const elemento = document;

  //Pongo el estado a valor inicial
  const [coordenadaX, setCoordenadaX] = useState(valorInicialX);
  const [coordenadaY, setCoordenadaY] = useState(valorInicialY);

const pintarCoordenadas =(e)=>{
  setCoordenadaX(e.x);
  setCoordenadaY(e.y);
}

  useEffect(()=>{
    elemento.addEventListener("mousemove",pintarCoordenadas);

    return () => {
      elemento.removeEventListener("mousemove", pintarCoordenadas);
    }; 
  },[]);



  return (
    <React.Fragment>
       <nav>
      <div className="nav-center">
        <ul className="nav-links" >
            <li id="coordenadas"> Coordenadas ðŸ‘‰ X: {coordenadaX} Y: {coordenadaY} </li>
        </ul>
      </div>
    </nav>

    </React.Fragment>
  );
}

export default Coordenadas;
