import React, { useState, useEffect } from "react";
import "../css/style.css";
import "../css/normaliza.css";
import { obtenerDatos } from "../Biblioteca/traerDatos.js";
import ListaPeliculas from "./ListaPeliculas.js";
import Pelicula from "./Pelicula";
import ListaActores from "./ListaActores.js";
import Actor from "./Actor.js";
import ListaVehiculos from "./ListaVehiculos.js";
import ListaNaves from "./ListaNaves.js";
import Vehiculo from "./Vehiculo.js"
import Nave from "./Nave.js";

function Peliculas() {
    //Variables e inicializando estados.
    const url = "https://swapi.py4e.com/api/films/";
    const valorInicial = [];
    const peliculaInicial = {};
    const actoresInicial = [];
    const actorInicial = {};
    const vehiclesInicial = [];
    const vehicleInicial = {};
    const navesInicial = [];
    const naveInicial = {};

    const [peliculas, setPeliculas] = useState(valorInicial);
    const [pelicula, setPelicula] = useState(peliculaInicial);
    const [actores, setActores] = useState(actoresInicial);
    const [actor, setActor] = useState(actorInicial);
    const [vehicles, setVehicles] = useState(vehiclesInicial);
    const [vehicle, setVehicle] = useState(vehicleInicial);
    const [naves, setNaves] = useState(navesInicial);
    const [nave, setNave] = useState(naveInicial);

    //Traer las películas
    const getPeliculas = async (origen) => {
        let datos = await obtenerDatos(origen);
        if (!datos.message) {
            setPeliculas(datos.results);
        }
    };

    const getDatosPelicula = async (origen) => {
        let datos = await obtenerDatos(origen);
        if (!datos.message) {
            setPelicula(datos); //Cambia el estado pinta los datos de la película seleccionada.
            setActor(actorInicial);
            setVehicles(vehiclesInicial);
            setVehicle(vehicleInicial);
            setNaves(navesInicial);
            setNave(naveInicial);
        }
    };

    useEffect(() => {
        getPeliculas(url);
    }, []);

    const getActores = async (origen) => {
        let datos = await obtenerDatos(origen);
        //Mapea e introduce en un array todas las url de los actores.
        let arrayPromesas = datos.characters.map((v, i, a) => {
            return obtenerDatos(v);
        });
        //console.log(arrayPromesas)
        //Se consume la promesa de promesas con await.
        const valoresPromesa = await Promise.allSettled(arrayPromesas);
        setActores(valoresPromesa);
    };

    const getDatosActor = async (origen) => {
        let datos = await obtenerDatos(origen);
        setActor(datos);
    };


    const getVehicles = async (origen) => {
        let datos = await obtenerDatos(origen);
        console.log(datos)
        //Mapea e introduce en un array todas las url de los vehículos.
        let arrayPromesas = datos.vehicles.map((v, i, a) => {
            return obtenerDatos(v);
        }); //Se consume la promesa de promesas con await.
        const valoresPromesa = await Promise.allSettled(arrayPromesas);
        setVehicles(valoresPromesa); //Pinta
    };
    
    const getNaves = async (origen) => {
        let datos = await obtenerDatos(origen);
        console.log(datos)
        //Mapea e introduce en un array todas las url de los vehículos.
        let arrayPromesas = datos.starships.map((v, i, a) => {
            return obtenerDatos(v);
        }); //Se consume la promesa de promesas con await.
        const valoresPromesa = await Promise.allSettled(arrayPromesas);
        setNaves(valoresPromesa); //Pinta
    };

    //Función que recibe una url de un vehículo y una referencia al DOM.
    const getVehicle = async(origen) =>{
    let datos= await obtenerDatos(origen);//Se consume la promesa con await.
     setVehicle(datos);
   };
  
   //Función que recibe una url de una nave y una referencia al DOM.
   const getNave = async(origen) =>{
    let datos= await obtenerDatos(origen);//Se consume la promesa con await.
     setNave(datos);
   };
  

    return (
        <React.Fragment>
            {/* Div donde de se pintan la lista de películas */}
            <div id="box1" className="item1">
                <ListaPeliculas
                    datos={peliculas}
                    funcion={getDatosPelicula}
                    datosActores={actores}
                    funcionActores={getActores}
                />
            </div>
            {/* Div donde de se pinta la película seleccionada */}
            <div id="box2" className="item1">
                {Object.keys(pelicula).length !== 0 ? (
                    <Pelicula datos={pelicula} /> //Cambia el estado de película y se pinta
                ) : (
                    <p>Selecciona una Película de la lista.</p>
                )}
            </div>
            {/* Div donde de se pintan la lista de Actores */}
            <div id="box3" className="item2">
                {Object.keys(actores).length !== 0 ? (
                    <ListaActores datosActores={actores} funcion={getDatosActor} datos={vehicles} funcionVehicle={getVehicles} datosNaves={naves} funcionNaves={getNaves}/> 
                ) : (
                    <p className="alert">No hay Actores en esta película</p>
                )}
            </div>
            {/* Div donde de se pintan las características del actor seleccionado.*/}
            <div id="box4" className="item2">
                {Object.keys(actor).length !== 0 ? (
                    <Actor datos={actor} />
                ) : (
                    <p className="alert">Selecciona un Actor</p>
                )}
            </div>
            {/* Div donde de se pintan la lista de vehículos del actor seleccionado.*/}
            <div id="box5" className="item2">
                {Object.keys(vehicles).length !== 0 ? (
                    <ListaVehiculos datos={vehicles} funcionVehicle={getVehicle}/>
                ) : (
                    <p className="alert">Selecciona un Vehículo</p>
                )}
            </div>

             {/* Div donde de se pintan la lista de naves del actor seleccionado.*/}
             <div id="box6" className="item2">
                {Object.keys(naves).length !== 0 ? (
                    <ListaNaves datos={naves} funcionNave={getNave}/>
                ) : (
                    <p className="alert">Selecciona una Nave</p>
                )}
            </div>

            {/* Div donde de se pintan las características del vehículo.*/}
            <div id="box7" className="item2">
                {Object.keys(vehicle).length !== 0 ? (
                    <Vehiculo datos={vehicle} />
                ) : (
                    <p className="alert">Vehículo</p>
                )}
            </div>

            {/* Div donde de se pintan las características dela nave.*/}
            <div id="box8" className="item2">
                {Object.keys(nave).length !== 0 ? (
                    <Nave datos={nave} />
                ) : (
                    <p className="alert">Nave</p>
                )}
            </div>
        </React.Fragment>
    );
}

export default Peliculas;
