import React from "react";
import PeliculasJson from './Ejercicio2.json';
import Pelicula from './Pelicula';
import Botones from "../Ejercicio1_304/Botones";
import '../Ejercicio1/style.css'

function Peliculas(props){

    return(
        <React.Fragment>
            {PeliculasJson.peliculas.map((v) => {
                return (

                        <Pelicula
                            key = {v.id}
                            titulo = {v.nombre}
                            director = {v.director}
                            clasificacion = {v.clasificacion}
                            cartelera = {v.cartelera}
                            nota = {v.nota}>
                            <div className='information'> 
                                {v.resumen}
                            </div>
                            <Botones></Botones>
                            <div className="divele" >
                                {v.elenco}
                            </div>
                            <div className="divext">
                                {v.extras}
                            </div>


                        </Pelicula>   
                );
            })}

            </React.Fragment>

    );
}

export default Peliculas;
