import React, { useState } from "react";
import { comprobarApuesta} from "../Biblioteca/Aleatorios/aleatorios.js"


function Euromillon(){

    const estadoInicial = [];
    const winner = " Números: 5, 45, 21 , 34 , 27, Estrellas: 6, 12";
    var premiado;
 
    const[apuesta, setApuestas] = useState(estadoInicial);

    const addObjeto = ()=>{

      var jugado =  comprobarApuesta();
       console.log(jugado[2]);
       console.log(typeof(jugado[2]))
       
        setApuestas([...apuesta, jugado]);
        premiado = jugado[2];
    }

    const limpiarApuestas = () => {
      setApuestas([]);
    };


    return(

       <React.Fragment>
<button onClick={addObjeto}>Añadir objeto</button>
<button onClick={limpiarApuestas}>Limpiar</button>

       <div className="estado">
          <p>Valor del estado actual.</p>
          
        
         
          {apuesta.map((valor) => {
            
                return <div className="nopremiada" key={valor}> 
                <p>{valor+ " no " + valor[6]} </p>
                <p>{winner} </p>
                </div>;
            
           if(premiado){
              return <div className="premiada"key={valor}>
                <p>{valor + " si  "}</p>
                <p>{winner} </p>
                </div>;
           }
          })}
           
          {/* <pre>{JSON.stringify(apuesta,null,2)}</pre> */}
        </div>


       </React.Fragment>

    )
};


export default Euromillon;
