import React, { useState, useEffect } from 'react';
import "../css/style.css";
import "../css/normaliza.css";
import ListaActores from './ListaActores.js';
import { obtenerDatos} from "../Biblioteca/traerDatos.js";

function Actores(props) {

  const actoresInicial = [];
  const actorInicial = {};
  const [actores, setActores] = useState(actoresInicial);
  const [actor, setActor] = useState(actorInicial);
  const url = "https://swapi.py4e.com/api/films/";
  


  const getActores = async(url) =>{
    let actores= await obtenerDatos(url);
    console.log(actores)
    //Mapea e introduce en un array todas las url de los actores.
    let arrayPromesas = actores.characters.map((v,i,a)=>{
      console.log(v);
      return obtenerDatos(v);
    });
    //Se consume la promesa de promesas con await.
    const valoresPromesa = await Promise.allSettled(arrayPromesas);
    console.log(valoresPromesa)
        setActores(valoresPromesa);
    };

    const getDatosActor = async (url)=>{
      let datos = await obtenerDatos(url);
      if(!datos.message){
          setActor(datos);
      }
    };
    useEffect(()=>{
      getActors(url);
     
  },[]);
  
 

    return (
      <React.Fragment>

      <div id="box3" className="item2">
        {Object.keys(actores).length !== 0 ?(<ListaActores  datos={actores} funcion={getDatosActor}/>//Cambia el estado de película y se pinta
            ):(<p className='=alert'>No hay Actores en esta película</p>)}  
      </div>
        
        
      </React.Fragment>
    );
  }
  
  export default Actores;
