import React, { useState, useEffect } from "react";
import{colorRgb} from "../Biblioteca/colores.js";

function ColoresRGB() {

  //Valor inicial a vacÃ­o;
  const valorInicial = "";
  const textoInicial = "";
  

  //Pongo el estado a valor inicial
  const [color, setColor] = useState(valorInicial);
  const [texto, setTexto] = useState(textoInicial);

 
  const pintar = ()=>{
    let rgb = colorRgb() ;
    setColor(rgb);
    setTexto(rgb);
   console.log(document.body)
    window.document.body.style.background = color;

  }


  useEffect(()=>{
   window.addEventListener("click", pintar);
    return () => {
      window.removeEventListener("click", pintar);
    }; 
  },[color]); //NO funciona si la dependencia 

  

  return (
    <React.Fragment>
      <nav>
      <div className="nav-center">
        <ul className="nav-links">
          <li><a href="Colores.js">Random RGBA</a></li>
          <li><a href="Colores.js">Random HEX</a></li>
          <li><a href="#"> ðŸ‘ˆ Elige tu formato</a></li>
        </ul>
      </div>
    </nav>
  
      <div className="container">
        <h2>background color RGB : <span className="color">{texto}</span></h2>
      </div>
      
    </React.Fragment>
  );
}

export default ColoresRGB;
