//Referencia a document.
const doc = window.document;

//Variable que asigna todo el body del html.
const e = document.body;

// Evento que se ejecuta al mover el mouse sobre el area previamente seleccionada.
const pintarCoordenadas = ("mousemove",(e)=>{

    return e.x;

});


export{pintarCoordenadas}
