import React, { useState } from "react";
import { comprobarApuesta} from "../Biblioteca/Aleatorios/aleatorios.js"


function Euromillon(){

    const estadoInicial = [];
    const winner = " Números: 5, 45, 21 , 34 , 27, Estrellas: 6, 12";
    const premiado= false;
 
    const[apuesta, setApuestas] = useState(estadoInicial);

    const addObjeto = ()=>{
        setApuestas([...apuesta,comprobarApuesta()])
      for (let i = 0; i < apuesta.length; i++) {
       premiado = apuesta[2] ? false: true;
      }

    }

    const limpiarApuestas = () => {
      setApuestas([]);
    };


    return(

       <React.Fragment>
<button onClick={addObjeto}>Añadir objeto</button>
<button onClick={limpiarApuestas}>Limpiar</button>

       <div className="estado">
          <p>Valor del estado actual.</p>

          {apuesta.map((valor) => {
            if(!premiado){
                return <div class="nopremiada" key={valor}> 
                <p>{valor + " no " + premiado} </p>
                <p>{winner} </p>
                </div>;
            }
            return <div class="premiada"key={valor}>
              <p>{valor + " si  " + premiado}</p>
              <p>{winner} </p>
              </div>;
              
          })}
         
          {/* <pre>{JSON.stringify(apuesta,null,2)}</pre> */}
        </div>


       </React.Fragment>

    )
};


export default Euromillon;
