import React, { useState, useEffect } from "react";

function ListaPeliculas(props) {
  return (
    <React.Fragment>
      <h1>Películas</h1>
      <ul>
        {props.datos.length !== 0
          ? props.datos.map((v, i, a) => {
            if(i<10)
            return (
              <li
                key={i}
                onClick={() => {
                  props.funcion(v.url);
                }}
              >
                {v.title}
              </li>
            );
          })
          : (<p className ="alert">No se han encontrado Películas</p>)}
      </ul>
    </React.Fragment>
  );
}

export default ListaPeliculas;
