import React, { useState } from "react";
import { comprobarApuesta } from "../Biblioteca/Aleatorios/aleatorios.js";

function Euromillon() {
  const estadoInicial = [];
  const winner = " Números: 5, 45, 21 , 34 , 27, Estrellas: 6, 12";
  var premiado;

  const [apuesta, setApuestas] = useState(estadoInicial);

  const addObjeto = () => {
    var jugado = comprobarApuesta();
    console.log(jugado[2]);
    console.log(typeof jugado[2]);

    setApuestas([...apuesta, jugado]);
   
  };

  const limpiarApuestas = () => {
    setApuestas([]);
  };

  return (
    <React.Fragment>
       <p>Euromillón</p>
      <div class= "botones">
      <button class="btnApuestas" onClick={addObjeto}>Añadir objeto</button>
      <button class="btnApuestas" onClick={limpiarApuestas}>Limpiar</button>
      </div>

      <div className="estado">
        {apuesta.map((valor) => {
          if (valor[2] === false) {
            return (
              <div className="nopremiada" key={valor}>
                <p>{"Apuesta: "+valor} </p>
                <p>{winner} </p>
              </div>
            );
          }
          if (valor[2] === true) {
            return (
              <div className="premiada" key={valor}>
                <p>{ "Apuesta: "+valor}</p>
                <p>{winner} </p>
                <p>Enhorabuena!! tu boleto ha sido premiado</p>
              </div>
            );
          }
        })}

        {/* <pre>{JSON.stringify(apuesta,null,2)}</pre> */}
      </div>
    </React.Fragment>
  );
}

export default Euromillon;
